<?php declare(strict_types=1);

use PHPUnit\Framework\TestCase;

require_once("model/contact-form/WizrdsContactForm.php");

class WizrdsContactFormTest extends TestCase {

    public function getFormDataOrErrorMessage(array $formData) {
        try {
            $wizrdsForm = new WizrdsContactForm(...$formData);
            return $wizrdsForm->getFormData();
        } catch (Exception $err) {
            return $err->getMessage();
        }
    }

    public function testGetFormData() {
        $test1 = array("", "John Doe", "john-doe@gmail.com", "website development", "");
        $test2 = array("Some Org", "John Doe", "john-doe@gmail.com", "invalid option", "some message");
        $test3 = array("Something", "James3", "james3@gmail.com", "design", "");
        $test4 = array("Something Else", "Jane", "jane@invalid", "app development", "");
        $test5 = array("Another", "Tom Riddle", "tom.riddle@yahoo.com", "website development", "WAY  Long");
        $test6 = array("Some Org", "Scarlett Johansson", "sj@hollywood.com", "app development", "I want to build an app to connect with more actors and actresses like me.");

        $result1 = $this->getFormDataOrErrorMessage($test1);
        $result2 = $this->getFormDataOrErrorMessage($test2);
        $result3 = $this->getFormDataOrErrorMessage($test3);
        $result4 = $this->getFormDataOrErrorMessage($test4);
        $result5 = $this->getFormDataOrErrorMessage($test5);
        $result6 = $this->getFormDataOrErrorMessage($test6);

        $this->assertEquals("The organization is required.", $result1);
        $this->assertEquals("The option \"invalid option\" is not a valid option for the product type.", $result2);
        $this->assertEquals("The name must only contain letters or spaces.", $result3);
        $this->assertEquals("The email address is invalid.", $result4);
        $this->assertEquals("The question or description must be between 1 and 2000 characters long.", $result5);
        $this->assertEquals(array("organization" => "Some Org", "name" => "Scarlett Johansson", "email" => "sj@hollywood.com", "productType" => "app development", "message" => "I want to build an app to connect with more actors and actresses like me."), $result6);
    }
}

?>